from flask import Flask, request, jsonifyimport joblibimport numpy as npimport cv2app = Flask(__name__)# Load Modelmodel = joblib.load("svm_model.pkl")@app.route("/", methods=["GET"])def home():    return "Backend is running!"@app.route("/predict", methods=["POST"])def predict():    if "file" not in request.files:        return jsonify({"error": "No file uploaded"}), 400    file = request.files["file"]    id_ = request.form.get("id")    name = request.form.get("name")    age = request.form.get("age")    if not id_ or not name or not age:        return jsonify({"error": "Missing patient details"}), 400    file_bytes = np.frombuffer(file.read(), np.uint8)    img = cv2.imdecode(file_bytes, cv2.IMREAD_GRAYSCALE)    img = cv2.resize(img, (64, 64)).flatten().reshape(1, -1)    prediction = model.predict(img)[0]    return jsonify({        "id": id_,        "name": name,        "age": age,        "prediction": int(prediction)    })if __name__ == "__main__":    app.run(debug=True)